mapper.select:
SELECT
FROM member WHERE member_id = #{memberId}
AND member_pw=PASSWORD(#{memberPw})

mapper.update:
UPDATE member SET
	member_id = #{memberId}

    ,member_pw = PASSWORD(#{memberPw})


	,member_name = #{memberName}


	,member_age = #{memberAge}


	,member_area = #{memberArea}


	,member_hp = #{hp}


	,member_sex = #{memberSex}


	,token = #{token}

WHERE
	member_id = #{memberId}
